import { createSlide } from '@reduxjs/toolkit';

const initialToggle = {toggle: false}

const toggleSlice = createSlide({
  name: 'toggle',
  initialstate: initialToggle,
  reducer:{
    on(state){
      state.initialToggle = true
    },
    off(state){
      state.initialToggle =false
    }
  }
})


export const toggleActions = toggleSlice.actions
export default toggleSlice;
